import drawing from './beedrawing.png';
import './App.css';

let canvas;
let context;

let bee = {
  xpos: 20,
  ypos: 20,
  // xvelocity,
  // yvelocity,
  img: new Image()
};

function update() {
  setTimeout(() => {
    canvas.width = document.body.offsetWidth;
    canvas.height = document.body.offsetHeight + 27;
    context.clearRect(0, 0, canvas.width, canvas.height);

    context.drawImage(bee.img, bee.xpos, bee.ypos, bee.img.width, bee.img.height);

    update();
  }, 5)
}

function App() {

  canvas = document.getElementById("bee-game");
  context = canvas.getContext("2d");

  canvas.width = document.body.offsetWidth;
  canvas.height = document.body.offsetHeight + 27;

  bee.img.src = drawing;

  return (
    <div className="App">
      <header className="App-header">
        <canvas id="bee-game"></canvas>
      </header>
    </div>
  );
}

export default App;
