class App extends React.Component {


  componentDidMount() {

    console.log(beePopulation);

    const canvas = this.refs.canvas;
    const ctx = canvas.getContext("2d");

    bee.img.src = drawing;

    canvas.width = document.body.offsetWidth;
    canvas.height = document.body.offsetHeight + 27;

    bees = new Array();
    var i;
    for(i = 0; i < beePopulation; i++){
      bees.push(new bee());
      bees[i].xpos = canvas.width * Math.random();
      bees[i].ypos = canvas.height * Math.random();
    }

    bee.img.onload = () => {
      var i;
      for(i = 0; i < beePopulation; i++){
        ctx.save();
        ctx.drawImage(bee.img, bees[i].xpos, bees[i].ypos, bees.img.width * 0.25, bee.img.height * 0.25)
        ctx.restore();
      }

    }

    this.Update(canvas, ctx);
  }

  Update(canvas, ctx) {
    //console.log("update");
    setTimeout(() => {
      //console.log("timeout");
      canvas.width = document.body.offsetWidth;
      canvas.height = document.body.offsetHeight;
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      var i;
      for(i = 0; i < beePopulation; i++){
        ctx.save();
        ctx.drawImage(bee.img, bees[i].xpos + 5 * Math.random(), bees[i].ypos + 5 * Math.random(), bee.img.width * 0.25, bee.img.height * 0.25);
        bees[i].xpos += bees[i].xvelocity;
        bees[i].ypos += bees[i].yvelocity;
        ctx.restore();
      }

      // bee.xpos += 3 * (Math.random() - 0.5);
      // bee.ypos += 3 * (Math.random() - 0.5);
      // ctx.drawImage(bee.img, bee.xpos, bee.ypos, bee.img.width * 0.25, bee.img.height * 0.25);

      this.Update(canvas, ctx);
    }, 5)
  }

  render() {
      return(
        <div className="App">
          <header className="App-header">
            <canvas ref="canvas" id="bee-game" />
          </header>
        </div>
      )
    }
  }

export default App;
