import drawing from './beedrawing.png';
import './App.css';
import React from 'react';

let canvas;
let ctx;

let bee = {
  xpos: 20,
  ypos: 20,
  xvelocity: 1,
  // yvelocity,
  img: new Image()
};

async function InitBees() {
  console.log("init");

  canvas = document.getElementById("bee-game");

  if(canvas == null){
    console.log("null");
  }

  ctx = await canvas.getContext("2d");

  canvas.width = document.body.offsetWidth;
  canvas.height = document.body.offsetHeight + 27;

  // console.log(canvas.width);
  // console.log(canvas.height);

  bee.img.src = drawing;

  Update();

}

function Update() {
  //console.log("update");
  setTimeout(() => {
    //console.log("timeout");
    canvas.width = document.body.offsetWidth;
    canvas.height = document.body.offsetHeight;
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    ctx.drawImage(bee.img, bee.xpos, bee.ypos, bee.img.width * 0.25, bee.img.height * 0.25);
    bee.xpos += bee.xvelocity;

    Update();
  }, 5)
}

