class App extends React.Component {


  componentDidMount() {

    const canvas = this.refs.canvas;
    const ctx = canvas.getContext("2d");

    bee.img.src = drawing;
    bee.image.width = bee.image.width * 0.25;
    bee.image.height = bee.image.height * 0.25;

    canvas.width = document.body.offsetWidth;
    canvas.height = document.body.offsetHeight + 27;

    bee.img.onload = () => {
      ctx.drawImage(bee.img, 0, 0)
    }

    this.Update(canvas, ctx);
  }

  Update(canvas, ctx) {
    //console.log("update");
    setTimeout(() => {
      //console.log("timeout");
      canvas.width = document.body.offsetWidth;
      canvas.height = document.body.offsetHeight;
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      ctx.drawImage(bee.img, bee.xpos, bee.ypos);
      bee.xpos += bee.xvelocity;

      this.Update(canvas, ctx);
    }, 5)
  }

  render() {
      return(
        <div className="App">
          <header className="App-header">
            <canvas ref="canvas" id="bee-game" />
          </header>
        </div>
      )
    }
  }

