import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

let canvas;
let context;

let bee = {
  xpos: 20,
  ypos: 20,
  xvelocity: 1,
  // yvelocity,
  img: new Image()
};

async function InitBees() {
  console.log("init");

  canvas = document.getElementById("bee-game");

  if(canvas == null){
    console.log("null");
  }

  context = await canvas.getContext("2d");

  canvas.width = document.body.offsetWidth;
  canvas.height = document.body.offsetHeight + 27;

  // console.log(canvas.width);
  // console.log(canvas.height);

  bee.img.src = drawing;

  Update();

}

function Update() {
  //console.log("update");
  setTimeout(() => {
    //console.log("timeout");
    canvas.width = document.body.offsetWidth;
    canvas.height = document.body.offsetHeight;
    context.clearRect(0, 0, canvas.width, canvas.height);

    context.drawImage(bee.img, bee.xpos, bee.ypos, bee.img.width * 0.25, bee.img.height * 0.25);
    bee.xpos += bee.xvelocity;

    Update();
  }, 1)
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
InitBees();



// If you want to start measuring performance in your app, pass a function
