import drawing from './beedrawing.png';
import './App.css';
import React from 'react';

let canvas;
let context;

let bee = {
  xpos: 20,
  ypos: 20,
  xvelocity: 1,
  // yvelocity,
  img: new Image()
};

async function InitBees() {
  console.log("init");

  canvas = document.getElementById("bee-game");

  if(canvas == null){
    console.log("null");
  }

  context = await canvas.getContext("2d");

  canvas.width = document.body.offsetWidth;
  canvas.height = document.body.offsetHeight + 27;

  // console.log(canvas.width);
  // console.log(canvas.height);

  bee.img.src = drawing;

  Update();

}

function Update() {
  //console.log("update");
  setTimeout(() => {
    //console.log("timeout");
    canvas.width = document.body.offsetWidth;
    canvas.height = document.body.offsetHeight;
    context.clearRect(0, 0, canvas.width, canvas.height);

    context.drawImage(bee.img, bee.xpos, bee.ypos, bee.img.width * 0.25, bee.img.height * 0.25);
    bee.xpos += bee.xvelocity;

    Update();
  }, 5)
}

function App() {

  // canvas = document.getElementById("bee-game");
  // context = canvas.getContext("2d");

  // canvas.width = document.body.offsetWidth;
  // canvas.height = document.body.offsetHeight + 27;

  // bee.img.src = drawing;
  console.log("app");

  InitBees();

  return (
    <div className="App">
      <header className="App-header">
        <canvas id="bee-game" />
      </header>
    </div>
  );
}

export default App;
